PAM_FILE="/etc/pam.d/system-auth"
LOCKOUT_THRESHOLD=5

# 점검 결과 초기화
RESULTS=()
OVERALL_STATUS="Good"
ISSUE_COUNT=0

# PAM 파일 존재 여부 점검
if [ ! -f "$PAM_FILE" ]; then
    RESULTS+=("PAM 파일이 존재하지 않습니다: $PAM_FILE")
    OVERALL_STATUS="Vulnerable"
    ISSUE_COUNT=$((ISSUE_COUNT + 1))
else
    # 계정 잠금 임계값 설정 점검
    if grep -q "deny=$LOCKOUT_THRESHOLD" "$PAM_FILE"; then
        RESULTS+=("계정 잠금 임계값이 올바르게 설정되어 있습니다: $LOCKOUT_THRESHOLD")
    else
        RESULTS+=("계정 잠금 임계값이 설정되어 있지 않거나 잘못 설정되어 있습니다.")
        OVERALL_STATUS="Vulnerable"
        ISSUE_COUNT=$((ISSUE_COUNT + 1))
    fi
fi

# 결과 리포트 출력
echo "=== 계정 잠금 임계값 설정 점검 결과 ==="
echo "점검 시간: $(date)"
