# 점검 결과 출력
for i in "${!RESULTS[@]}"; do
    echo "$((i + 1)). ${RESULTS[$i]}"
    if [[ ${RESULTS[$i]} == *"올바르게 설정되어 있습니다"* ]]; then
        echo "   [PASS]"
    else
        echo "   [FAIL]"
    fi
done

# 요약 섹션
echo "=== 점검 결과 요약 ==="
